import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import '../Styles/Login.scss';
import { EyeInvisibleOutlined, EyeOutlined } from '@ant-design/icons';
import { toast } from 'react-toastify';

const Login = ({ setAuth }) => {
  const URL = process.env.REACT_APP_API_URL;
  const [inputs, setInputs] = useState({
    email: '',
    password: '',
  });
  const [visible, setVisible] = useState(true);
  const { email, password } = inputs;

  const onChange = (e) =>
    setInputs({ ...inputs, [e.target.name]: e.target.value });

  const onSubmitForm = async (e) => {
    e.preventDefault();
    try {
      const body = { email, password };
      const response = await fetch(`${URL}/auth/login`, {
        method: 'POST',
        headers: {
          'Content-type': 'application/json',
        },
        body: JSON.stringify(body),
      });

      const parseRes = await response.json();

      if (parseRes.jwtToken) {
        localStorage.setItem('token', parseRes.jwtToken);
        setAuth(true);
        toast.success('Logged in Successfully');
      } else {
        setAuth(false);
        toast.error(parseRes);
      }
    } catch (err) {
      console.error(err.message);
    }
  };

  return (
    <div>
      <h1
        id='FormBlurb'
        className='font-fonts text-rose-700 text-5xl font-semibold content-center'
      >
        Login
      </h1>
      <div id='LoginForm'>
        <h1
          id='FormBlurb'
          className='font-fonts text-rose-700 text-5xl font-semibold content-center'
        >
          Login
        </h1>
        <form className='NewUserForm' onSubmit={onSubmitForm}>
          <label htmlFor='email'>Email:</label>
          <input
            type='text'
            name='email'
            placeholder='email'
            value={email}
            onChange={(e) => onChange(e)}
            className='form-control my-3'
          />
          <label htmlFor='password'>Password:</label>
          <input
            type={visible ? 'text' : 'password'}
            name='password'
            placeholder='password'
            value={password}
            onChange={(e) => onChange(e)}
            className='form-control my-3'
          />
          <div onClick={() => setVisible(!visible)}>
            {visible ? <EyeOutlined /> : <EyeInvisibleOutlined />}
          </div>
          <button id='submitBtn' className='btn btn-success btn-block'>
            Submit
          </button>
        </form>

        <Link to='/register'>
          <button id='register'>Register</button>
        </Link>
        <Link to={'/videoGames'}>
          <button id='backNew'>Back</button>
        </Link>
      </div>
    </div>
  );
};

export default Login;
