import 'react-toastify/dist/ReactToastify.css';
import { toast } from 'react-toastify';
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Switch,
  Redirect,
} from 'react-router-dom';
import React, { useEffect, useState } from 'react';

//Pages
import Login from './Components/Login.js';
import Register from './Components/Register.js';
import Dashboard from './Components/Dashboard.js';
import HomePage from './Pages/home.js';
import GameGallery from './Pages/gallery.js';
import NavBar from './Components/navBar.js';
import UsersProfilePage from './Pages/usersProfilePage.js';
import ShowGame from './Pages/showGamePage.js';
import NewUserPage from './Pages/userSignUpPage.js';
import EditUserPage from './Pages/userEditPage.js';

toast.configure();
const App = () => {
  const checkAuthenticated = async () => {
    try {
      const res = await fetch('http://localhost:5000/authentication/verify', {
        method: 'POST',
        headers: { jwt_token: localStorage.token },
      });

      const parseRes = await res.json();

      parseRes === true ? setIsAuthenticated(true) : setIsAuthenticated(false);
    } catch (err) {
      console.error(err.message);
    }
  };

  useEffect(() => {
    checkAuthenticated();
  }, []);

  const [isAuthenticated, setIsAuthenticated] = useState(false);

  const setAuth = (boolean) => {
    setIsAuthenticated(boolean);
  };
  return (
    <BrowserRouter>
      <NavBar />
      <main>
        <Router>
          <Routes>
            <Switch>
              <Route
                exact
                path='/login'
                render={(props) =>
                  !isAuthenticated ? (
                    <Login {...props} setAuth={setAuth} />
                  ) : (
                    <Redirect to='/dashboard' />
                  )
                }
              />
              <Route
                exact
                path='/register'
                render={(props) =>
                  !isAuthenticated ? (
                    <Register {...props} setAuth={setAuth} />
                  ) : (
                    <Redirect to='/dashboard' />
                  )
                }
              />
              <Route
                exact
                path='/dashboard'
                render={(props) =>
                  isAuthenticated ? (
                    <Dashboard {...props} setAuth={setAuth} />
                  ) : (
                    <Redirect to='/login' />
                  )
                }
              />
              <Route path='/' element={<HomePage />} />
              {/*VideoGames */}
              <Route path='/videoGames' element={<GameGallery />} />
              <Route path='/videoGames/:id' element={<ShowGame />} />
              {/*Users */}
              <Route path='/users/:id' element={<UsersProfilePage />} />
              {/* <Route path='users/new' element={<NewUserPage />} /> */}
              <Route path='users/:id/edit' element={<EditUserPage />} />
              <Route
                path='users/new'
                element={<NewUserPage setUser={setUser} />}
              />
            </Switch>
          </Routes>
        </Router>
      </main>
    </BrowserRouter>
  );
};
export default App;
